$(document).ready(function() {
  var image = `data:image/png;base64,`;
  $('[data-toggle="tooltip"]').tooltip();
  $('.switcher').click(function() {
    $('.switcher img').toggleClass('rolate');
  });
  chrome.storage.local.get("lang_selected", function(e) {
    let lang_selected = "vi"; // default is vietnamese
    if (e.lang_selected) {
      lang_selected = e.lang_selected;
      $(".to").text(lang_selected);
    }
    $("#translate-text").click(function() {
      if ($('.switcher img').hasClass("rolate")) {
        get_result($('#text-input')[0].value, lang_selected, 'en');
      } else {
        get_result($('#text-input')[0].value, 'en', lang_selected);
      }
    });
    $('#text-input').keypress(function(event) {
      if (event.which === 13) {
        if ($('.switcher img').hasClass("rolate")) {
          get_result($('#text-input')[0].value, lang_selected, 'en');
        } else {
          get_result($('#text-input')[0].value, 'en', lang_selected);
        }
      }
    });
    chrome.storage.local.get('selection', (data) => {
      if (data.selection) {
        $('#text-input')[0].value = data.selection;
        get_result(data.selection, "en", lang_selected);
        $('.result').slideDown();
        chrome.storage.local.set({ 'selection': "" }, () => {});
      }
    });
  })

  function get_result(str, from, to) {
    if (str.length <= 150) {
      $('.en').html('<i class="speak" data-src="' + from + '" speak-for="' + str + '"></i> ' + str);
    } else {
      $('.en').html(str);
    }
    $('.vi').html(`<img src="` + image + `">`);
    if ($('#text-input')[0].value == "") {
      $('.result').slideUp(50);
    } else {
      $('.result').slideDown(50);
    }
    let url = "https://translation.googleapis.com/language/translate/v2?key=AIzaSyBS3kL-NT547cm3dyi6ooj2XYtarN7w5tc";
    url += "&source=" + from;
    url += "&target=" + to;
    url += "&q=" + encodeURIComponent(str);
    $.get(url, function(data, status) {
      let target = data.data.translations[0].translatedText;
      if (str.length <= 150) {
        $('.vi').html('<i class="speak" data-src="' + to + '" speak-for="' + target + '"></i> ' + target);
        get_speak();
      } else {
        $('.vi').html(target);
      }
      append_addmore(to, encodeURIComponent(str));
      $('[data-toggle="tooltip"]').tooltip();
    });

  }

  function get_speak() {
    $('.speak').click(function() {
      let from = $(this).attr('data-src');
      let str = $(this).attr('speak-for');
      let url = "https://translate.googleapis.com/translate_tts?client=gtx&ie=UTF-8&tl=" + from + "&q=" + encodeURIComponent(String(str));
      let au = $('<audio>', {
        'src': url
      })
      au[0].play();
    });
  };

  function append_addmore(to, target) {
    if ($('p').length < 1) {
      let p = $('<p>', {
        "data-toggle": "tooltip",
        "data-placement": "left",
        "title": "Click to open translate.google.com"
      })
      p.text("Read more..");
      p.attr("href", "https://translate.google.com/?hl="+to+"#auto" + "/" + to + "/" + target);
      $('.more').append(p);
    } else {
      $('p').attr("href", "https://translate.google.com/?hl="+to+"#auto"+ "/" + to + "/" + target);
    }
    if (!target) {
      $('p').remove();
    }
    $('p').click(function() {
      window.open($('p').attr('href'), '_blank');
    })
  }
})